//test.js
var stocksnap = require('./index');

//Getting a list of thumbnails, sorted by most downloaded downloads.
stocksnap(null, {sort: 'downloads'}, function (snaps) {
  console.log('Sorted by downloads', snaps);
});

//Getting pictures of doors
stocksnap('doors', null, function (snaps) {
  console.log('Pictures of doors', snaps);
});

//Getting 2 pages worth sorted by views of highres pictures of apples, with the array shuffled
stocksnap('apple', {highres: true, sort: 'views', shuffle: true, pages: 2}, function (snaps) {
  console.log('Oddly specific apple query', snaps);
});

//Getting just one photo of a girl
stocksnap('girl', {one: true}, function (snaps) {
   console.log('Just a girl', snaps);
});
